srcDoc.css:
body {
  font-family: Times New Roman;
  background: #f5f5f3;
  margin: 0;
  padding: 20px;
}

.dashboard {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
  gap: 20px;
}

.card {
  background: #fff;
  border-radius: 12px;
  padding: 16px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
}

.card h3 {
  font-size: 14px;
  color: #374151;
  margin-bottom: 12px;
}

.big-metric {
  font-size: 24px;
  font-weight: 600;
  color: #111827;
}

.trend {
  font-size: 14px;
  margin-bottom: 8px;
}

.trend.positive {
  color: #16a34a;
}

.trend.negative {
  color: #dc2626;
}

