// Sessions by device (Pie)
new Chart(document.getElementById("sessionsDevice"), {
  type: "doughnut",
  data: {
    labels: ["Mobile", "Desktop", "Tablet", "Other"],
    datasets: [{
      data: [35000, 32000, 26000, 9000],
      backgroundColor: ["#3b82f6", "#8b5cf6", "#10b981", "#f59e0b"]
    }]
  },
  options: {
    responsive: true,
    plugins: {
      legend: {
        position: "right"
      }
    }
  }
});

// Customers over time (Line)
new Chart(document.getElementById("customersOverTime"), {
  type: "line",
  data: {
    labels: ["Jun 6", "Jun 14", "Jun 22", "Jul 5"],
    datasets: [{
      label: "First time",
      data: [2000, 3500, 5000, 4000],
      borderColor: "#3b82f6",
      fill: false
    }, {
      label: "Recurring",
      data: [1500, 3000, 7000, 5000],
      borderColor: "#8b5cf6",
      fill: false
    }]
  },
  options: {
    responsive: true,
    tension: 0.4
  }
});

// Total sales trend (Line small)
new Chart(document.getElementById("totalSalesTrend"), {
  type: "line",
  data: {
    labels: ["Jun 6", "Jun 14", "Jun 22", "Jul 5"],
    datasets: [{
      data: [5000, 8000, 7000, 10000],
      borderColor: "#06b6d4",
      fill: false
    }]
  },
  options: {
    responsive: true,
    plugins: {
      legend: {
        display: true
      }
    },
    scales: {
      x: {
        display: true
      },
      y: {
        display: true
      }
    }
  }
});

// Gross sales by country (Bar horizontal)
new Chart(document.getElementById("salesByCountry"), {
  type: "bar",
  data: {
    labels: ["United States", "Canada", "United Kingdom", "France", "Mexico"],
    datasets: [{
      data: [10000, 8000, 7000, 7500, 6000],
      backgroundColor: "#3b82f6"
    }]
  },
  options: {
    indexAxis: "y",
    plugins: {
      legend: {
        display: false
      }
    }
  }
});

// Sales by product name (Treemap substitute as Bar)
new Chart(document.getElementById("salesByProduct"), {
  type: "bar",
  data: {
    labels: ["Wool cap", "Crewneck", "Blouse", "T-shirt", "Long sleeve"],
    datasets: [{
      data: [419719, 251831, 167887, 75549, 75549],
      backgroundColor: ["#3b82f6", "#8b5cf6", "#2563eb", "#ec4899", "#10b981"]
    }]
  },
  options: {
    plugins: {
      legend: {
        display: false
      }
    }
  }
});

// Gross sales by device (Line multiple)
new Chart(document.getElementById("grossByDevice"), {
  type: "line",
  data: {
    labels: ["Jun 6", "Jun 14", "Jun 22", "Jul 5"],
    datasets: [{
      label: "Mobile",
      data: [4000, 8000, 7000, 9000],
      borderColor: "#3b82f6",
      fill: false
    }, {
      label: "Desktop",
      data: [3000, 6000, 5000, 7000],
      borderColor: "#8b5cf6",
      fill: false
    }, {
      label: "Tablet",
      data: [2000, 4000, 3000, 5000],
      borderColor: "#10b981",
      fill: false
    }]
  },
  options: {
    tension: 0.4
  }
});

// Sessions by country (Stacked bar)
new Chart(document.getElementById("sessionsByCountry"), {
  type: "bar",
  data: {
    labels: ["Jun 6", "Jun 14", "Jun 22", "Jul 5"],
    datasets: [{
      label: "United States",
      data: [2000, 3000, 2500, 4000],
      backgroundColor: "#3b82f6"
    }, {
      label: "Canada",
      data: [1000, 2000, 1500, 3000],
      backgroundColor: "#8b5cf6"
    }, {
      label: "United Kingdom",
      data: [1500, 2500, 2000, 3500],
      backgroundColor: "#ec4899"
    }, {
      label: "France",
      data: [1200, 2200, 1800, 2800],
      backgroundColor: "#10b981"
    }]
  },
  options: {
    responsive: true,
    plugins: {
      legend: {
        position: "bottom"
      }
    },
    scales: {
      x: {
        stacked: true
      },
      y: {
        stacked: true
      }
    }
  }
});

// Sessions over time (Grouped bar)
new Chart(document.getElementById("sessionsOverTime"), {
  type: "bar",
  data: {
    labels: ["Jun 6", "Jun 14", "Jun 22", "Jul 5"],
    datasets: [{
      label: "Jun 6–Jul 5, 2023",
      data: [7000, 5000, 6000, 8000],
      backgroundColor: "#3b82f6"
    }, {
      label: "Apr 22–Apr 28, 2024",
      data: [6000, 4000, 5000, 7000],
      backgroundColor: "#93c5fd"
    }]
  },
  options: {
    responsive: true
  }
});

