* { margin: 0; padding: 0; box-sizing: border-box; }

/* Body */
body {
  font-family: Times New Roman Regular;
  background-color: #f5f5f3;
  min-height: 30vh;
	margin: 0;
  padding-top: 90px; 
}

/* ---------------- Header ---------------- */

.header {
	position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 24px;
  background: #f5f5f3;
  padding: 16px 80px;
}
.logo img { height: 50px; }
.left-side { display: flex; align-items: center; gap: 30px; }
.nav-links { display: flex; gap: 20px; }
.nav-links a { text-decoration: none; color: #333; font-size: 20px; }
.right-side { display: flex; align-items: center; gap: 20px; }
.header-actions { display: flex; align-items: center; gap: 20px; }
.login-btn { background: none; border: none; color: #333; font-size: 16px; cursor: pointer; }
.start-btn { background: black; color: white; border: none; padding: 8px 16px; border-radius: 20px; font-size: 16px; cursor: pointer; }
.menu-toggle { display: none; font-size: 22px; background: none; border: none; cursor: pointer; }

/* Mobile */
.mobile-menu { display: none; flex-direction: column; background: white; padding: 16px; border-top: 1px solid #eee; }
.mobile-menu a { padding: 10px 0; color: #333; text-decoration: none; }
@media (max-width: 1024px) {
  .header { padding: 16px 20px; }
  .nav-links { display: none; }
  .menu-toggle { display: block; }
  .mobile-menu.show { display: flex; }
  .login-btn { display: none; }
}

/* ---------------- Hero + Filters ---------------- */
.container { background-color: #f5f5f3; margin: 0; padding: 0; }
.hero-section { background-color: #f5f5f3; padding: 60px 24px 0px 24px; text-align: center; max-width: 1200px; margin: 0 auto; }
.hero-tag { font-size: 16px; font-weight: 400; color: #000; letter-spacing: 0.05em; text-transform: uppercase; margin-bottom: 8px; }
.hero-title { font-size: 68px; font-weight: 400; color: #000; line-height: 1.1; margin-bottom: 16px; max-width: 800px; margin-left: auto; margin-right: auto; }
.hero-subtitle { font-size: 20px; color: #6b7280; margin-bottom: 32px; max-width: 600px; margin-left: auto; margin-right: auto; line-height: 1.5; }
.hero-button { background-color: #000; color: white; padding: 14px 28px; border: none; border-radius: 50px; font-size: 16px; font-weight: 400; cursor: pointer; transition: all 0.2s ease; display: inline-flex; align-items: center; gap: 8px; }
.hero-button:hover { background-color: #1f2937; transform: translateY(-1px); }

.filters-section { background-color: #f5f5f3; padding: 24px; margin: 20px 24px 0 24px; border-radius: 12px; max-width: 1200px; margin-left: auto; margin-right: auto; margin-top: 20px; margin-bottom: 0; }
.filters-row { display: grid; grid-template-columns: 2fr 1fr 1fr 1fr auto; gap: 20px; align-items: end; }
.filter-group { display: flex; flex-direction: column; gap: 8px; }
.filter-label { font-size: 14px; font-weight: 600; color: #374151; }
.date-range { display: flex; gap: 12px; align-items: center; }
.date-input { padding: 10px 12px; border: 1px solid #d1d5db; border-radius: 6px; font-size: 14px; color: #374151; background-color: white; min-width: 140px; }
.date-separator { color: #9ca3af; font-weight: 500; }
.filter-select { padding: 10px 12px; border: 1px solid #d1d5db; border-radius: 6px; font-size: 14px; color: #374151; background-color: white; cursor: pointer; min-width: 120px; appearance: none; background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%236b7280' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='M6 8l4 4 4-4'/%3e%3c/svg%3e"); background-position: right 8px center; background-repeat: no-repeat; background-size: 16px; padding-right: 32px; }
.filter-select:focus, .date-input:focus { outline: none; border-color: #3b82f6; box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1); }
.view-analytics-btn { background-color: #000; color: white; padding: 10px 24px; border: none; border-radius: 6px; font-size: 14px; font-weight: 500; cursor: pointer; transition: background-color 0.2s ease; height: fit-content; }
.view-analytics-btn:hover { background-color: #1f2937; }

/* Responsive design for filters */
@media (max-width: 1024px) and (min-width: 769px) {
  .filters-row { grid-template-columns: 1fr 1fr; gap: 20px; }
  .filter-group:first-child { grid-column: 1 / -1; }
  .filter-group:last-child { grid-column: 1 / -1; justify-self: center; margin-top: 8px; }
  .date-range { justify-content: center; max-width: 400px; margin: 0 auto; }
}
@media (max-width: 768px) {
