* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: Times New Roman;
  background-color: #f5f5f3;
  min-height: 30vh;
}

.container {
  background-color: #f5f5f3;
  margin: 0;
  padding: 0;
}

.hero-section {
  background-color: #f5f5f3;
  padding: 60px 24px 0px 24px;
  text-align: center;
  max-width: 1200px;
  margin: 0 auto;
}

.hero-tag {
  font-size: 20px;
  font-weight: 400;
  color: #6b7280;
  letter-spacing: 0.05em;
  text-transform: uppercase;
  margin-bottom: 16px;
}

.hero-title {
  font-size: 68px;
  font-weight: 400;
  color: #1f2937;
  line-height: 1.1;
  margin-bottom: 16px;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
}

.hero-subtitle {
  font-size: 20px;
  color: #6b7280;
  margin-bottom: 32px;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
  line-height: 1.5;
}

.hero-button {
  background-color: #000;
  color: white;
  padding: 14px 28px;
  border: none;
  border-radius: 50px;
  font-size: 16px;
  font-weight: 400;
  cursor: pointer;
  transition: all 0.2s ease;
  display: inline-flex;
  align-items: center;
  gap: 8px;
}

.hero-button:hover {
  background-color: #1f2937;
  transform: translateY(-1px);
}

.filters-section {
  background-color: #f5f5f3;
  padding: 24px;
  margin: 20px 24px 0 24px;
  border-radius: 12px;
  max-width: 1200px;
  margin-left: auto;
  margin-right: auto;
  margin-top: 20px;
  margin-bottom: 0;
}

.filters-row {
  display: grid;
  grid-template-columns: 2fr 1fr 1fr 1fr auto;
  gap: 20px;
  align-items: end;
}

.filter-group {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.filter-label {
  font-size: 14px;
  font-weight: 600;
  color: #374151;
}

.date-range {
  display: flex;
  gap: 12px;
  align-items: center;
}

.date-input {
  padding: 10px 12px;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 14px;
  color: #374151;
  background-color: white;
  min-width: 140px;
}

.date-separator {
  color: #9ca3af;
  font-weight: 500;
}

.filter-select {
  padding: 10px 12px;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 14px;
  color: #374151;
  background-color: white;
  cursor: pointer;
  min-width: 120px;
  appearance: none;
  background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%236b7280' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='M6 8l4 4 4-4'/%3e%3c/svg%3e");
  background-position: right 8px center;
  background-repeat: no-repeat;
  background-size: 16px;
  padding-right: 32px;
}

.filter-select:focus,
.date-input:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.view-analytics-btn {
  background-color: #000;
  color: white;
  padding: 10px 24px;
  border: none;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s ease;
  height: fit-content;
}

.view-analytics-btn:hover {
  background-color: #1f2937;
}

/* Responsive design */
@media (max-width: 1024px) and (min-width: 769px) {
  .filters-row {
    grid-template-columns: 1fr 1fr;
    gap: 20px;
  }
  .filter-group:first-child {
    grid-column: 1/-1;
  }
  .filter-group:last-child {
    grid-column: 1/-1;
    justify-self: center;
    margin-top: 8px;
  }
  .date-range {
    justify-content: center;
    max-width: 400px;
    margin: 0 auto;
  }
}
@media (max-width: 768px) {
  .hero-title {
    font-size: 32px;
    line-height: 1.2;
  }
  .hero-subtitle {
    font-size: 16px;
  }
  .hero-section {
    padding: 40px 16px 0px 16px;
  }
  .filters-section {
    margin: 20px 16px 0 16px;
    padding: 24px 20px;
  }
  .filters-row {
    display: flex;
    flex-direction: column;
    gap: 20px;
    align-items: stretch;
  }
  .filter-group {
    display: flex;
    flex-direction: column;
    gap: 10px;
    width: 100%;
  }
  .date-range {
    display: flex;
    flex-direction: column;
    gap: 12px;
    align-items: stretch;
  }
  .date-separator {
    display: none;
  }
  .date-input {
    width: 100%;
    min-width: auto;
    padding: 12px 16px;
    font-size: 16px;
  }
  .filter-select {
    width: 100%;
    padding: 12px 16px;
    font-size: 16px;
    min-width: auto;
  }
  .filter-label {
    font-size: 15px;
    font-weight: 600;
    margin-bottom: 2px;
  }
  .view-analytics-btn {
    width: 100%;
    padding: 14px 24px;
    font-size: 16px;
    margin-top: 8px;
  }
}

