// Initialize date inputs with today's date
function initializeDates() {
  const today = new Date();
  const thirtyDaysAgo = new Date();
  thirtyDaysAgo.setDate(today.getDate() - 30);
  const formatDate = date => {
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');
    const year = date.getFullYear();
    return `${month}/${day}/${year}`;
  };
  document.getElementById('startDate').value = formatDate(thirtyDaysAgo);
  document.getElementById('endDate').value = formatDate(today);
}
function viewAnalytics() {
  // This function can be connected to Appsmith navigation or API calls
  console.log('Navigating to analytics dashboard...');
  alert('Navigating to analytics dashboard...');
  // Example: navigateTo('AnalyticsDashboard');

  // For Appsmith integration, you can trigger:
  // - Navigate to another page
  // - Open a modal
  // - Trigger a query
  // - Update store values
}
function applyFilters() {
  const filters = {
    startDate: document.getElementById('startDate').value,
    endDate: document.getElementById('endDate').value,
    deviceType: document.getElementById('deviceType').value,
    country: document.getElementById('country').value,
    product: document.getElementById('product').value
  };
  console.log('Applied filters:', filters);
  alert('Filters applied! Check console for details.');

  // This can be connected to Appsmith queries
  // Example: RefreshAnalyticsQuery.run(filters);

  // Make filters available globally for Appsmith
  window.analyticsFilters = filters;

  // Trigger custom event that Appsmith can listen to
  window.dispatchEvent(new CustomEvent('filtersChanged', {
    detail: filters
  }));

  // You can also store filters in Appsmith store
  // storeValue('analyticsFilters', filters);
}

// Utility function to get current filters
function getCurrentFilters() {
  return {
    startDate: document.getElementById('startDate').value,
    endDate: document.getElementById('endDate').value,
    deviceType: document.getElementById('deviceType').value,
    country: document.getElementById('country').value,
    product: document.getElementById('product').value
  };
}

// Function to update filters programmatically (for Appsmith integration)
function updateFilters(newFilters) {
  if (newFilters.startDate) document.getElementById('startDate').value = newFilters.startDate;
  if (newFilters.endDate) document.getElementById('endDate').value = newFilters.endDate;
  if (newFilters.deviceType) document.getElementById('deviceType').value = newFilters.deviceType;
  if (newFilters.country) document.getElementById('country').value = newFilters.country;
  if (newFilters.product) document.getElementById('product').value = newFilters.product;

  // Auto-apply the new filters
  applyFilters();
}

// Function to reset filters to default
function resetFilters() {
  document.getElementById('deviceType').value = 'all';
  document.getElementById('country').value = 'all';
  document.getElementById('product').value = 'all';
  initializeDates();
  applyFilters();
}

// Make functions available globally for Appsmith integration
window.viewAnalytics = viewAnalytics;
window.applyFilters = applyFilters;
window.getCurrentFilters = getCurrentFilters;
window.updateFilters = updateFilters;
window.resetFilters = resetFilters;
window.getFilters = () => window.analyticsFilters;

// Initialize the component when DOM is loaded
document.addEventListener('DOMContentLoaded', function () {
  initializeDates();

  // Set up event listeners for auto-applying filters
  document.querySelectorAll('.filter-select, .date-input').forEach(element => {
    element.addEventListener('change', function () {
      // Auto-apply filters after a short delay
      setTimeout(applyFilters, 300);
    });
  });

  // Initialize filters object
  window.analyticsFilters = getCurrentFilters();
  console.log('Analytics hero section initialized');
});

// Additional utility functions for Appsmith integration
const AnalyticsUtils = {
  // Format date for display
  formatDate: date => {
    if (!date) return '';
    const d = new Date(date);
    const month = (d.getMonth() + 1).toString().padStart(2, '0');
    const day = d.getDate().toString().padStart(2, '0');
    const year = d.getFullYear();
    return `${month}/${day}/${year}`;
  },
  // Parse date from string
  parseDate: dateString => {
    if (!dateString) return null;
    const [month, day, year] = dateString.split('/');
    return new Date(year, month - 1, day);
  },
  // Validate date range
  validateDateRange: () => {
    const startDate = AnalyticsUtils.parseDate(document.getElementById('startDate').value);
    const endDate = AnalyticsUtils.parseDate(document.getElementById('endDate').value);
    if (startDate && endDate && startDate > endDate) {
      alert('Start date must be before end date');
      return false;
    }
    return true;
  },
  // Get date range in days
  getDateRangeInDays: () => {
    const startDate = AnalyticsUtils.parseDate(document.getElementById('startDate').value);
    const endDate = AnalyticsUtils.parseDate(document.getElementById('endDate').value);
    if (startDate && endDate) {
      const diffTime = Math.abs(endDate - startDate);
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      return diffDays;
    }
    return 0;
  }
};

// Make utility functions available globally
window.AnalyticsUtils = AnalyticsUtils;

