* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
  background: #f8fafc;
  padding: 16px;
  color: #1e293b;
}

.dashboard-container {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 16px;
  max-width: 1600px;
  margin: 0 auto;
}

.card {
  background: white;
  border-radius: 8px;
  padding: 20px;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
  border: 1px solid #e2e8f0;
  position: relative;
  height: 320px;
  display: flex;
  flex-direction: column;
}

.card-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;
  flex-shrink: 0;
}

.card-header h3 {
  font-size: 14px;
  font-weight: 600;
  color: #374151;
}

.menu-btn {
  background: none;
  border: none;
  font-size: 16px;
  color: #9ca3af;
  cursor: pointer;
  padding: 4px;
  border-radius: 4px;
}

.menu-btn:hover {
  background: #f3f4f6;
  color: #6b7280;
}

.chart-container {
  position: relative;
  flex: 1;
  min-height: 160px;
}

.chart-container.small-chart {
  min-height: 100px;
}

.donut-container {
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
}

.donut-center {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  z-index: 10;
}

.center-value {
  font-size: 20px;
  font-weight: 700;
  color: #1e293b;
}

.legend {
  margin-top: 16px;
  flex-shrink: 0;
}

.legend-item {
  display: flex;
  align-items: center;
  margin-bottom: 6px;
  font-size: 12px;
}

.legend-color {
  width: 10px;
  height: 10px;
  border-radius: 2px;
  margin-right: 8px;
  flex-shrink: 0;
}

.legend-item span:nth-child(2) {
  flex: 1;
  color: #6b7280;
}

.legend-value {
  color: #374151;
  font-weight: 500;
}

.more-item {
  color: #6b7280;
  font-size: 12px;
  margin-top: 4px;
}

.sales-summary {
  display: flex;
  align-items: baseline;
  gap: 12px;
  margin-bottom: 16px;
  flex-shrink: 0;
}

.sales-amount {
  font-size: 18px;
  font-weight: 700;
  color: #1e293b;
}

.sales-change {
  color: #059669;
  font-size: 12px;
  font-weight: 500;
  background: #d1fae5;
  padding: 2px 6px;
  border-radius: 4px;
}

.horizontal-bars {
  display: flex;
  flex-direction: column;
  gap: 12px;
  flex: 1;
  overflow-y: auto;
}

.bar-item {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 12px;
}

.bar-label {
  width: 80px;
  color: #6b7280;
  flex-shrink: 0;
  font-size: 11px;
}

.bar-container {
  flex: 1;
  height: 6px;
  background: #f1f5f9;
  border-radius: 3px;
  position: relative;
}

.bar-fill {
  height: 100%;
  border-radius: 3px;
  transition: width 0.3s ease;
}

.bar-value {
  color: #374151;
  font-weight: 500;
  min-width: 35px;
  text-align: right;
  font-size: 11px;
}

.product-list {
  margin-bottom: 16px;
  flex: 1;
  overflow-y: auto;
}

.product-item {
  display: flex;
  align-items: center;
  margin-bottom: 8px;
  font-size: 12px;
}

.product-color {
  width: 10px;
  height: 10px;
  border-radius: 2px;
  margin-right: 8px;
  flex-shrink: 0;
}

.product-name {
  flex: 1;
  color: #6b7280;
}

.product-value {
  color: #374151;
  font-weight: 500;
  font-size: 11px;
}

.product-bars {
  display: flex;
  gap: 1px;
  height: 6px;
  border-radius: 3px;
  overflow: hidden;
  flex-shrink: 0;
}

.color-bar {
  transition: flex 0.3s ease;
}

/* Responsive design */
@media (max-width: 1400px) {
  .dashboard-container {
    grid-template-columns: repeat(3, 1fr);
  }
}
@media (max-width: 1000px) {
  .dashboard-container {
    grid-template-columns: repeat(2, 1fr);
  }
}
@media (max-width: 600px) {
  .dashboard-container {
    grid-template-columns: 1fr;
    gap: 12px;
    padding: 12px;
  }
  .card {
    padding: 16px;
    height: 280px;
    min-height: 280px;
  }
  .bar-label {
    width: 70px;
    font-size: 10px;
  }
}
/* Hover effects */
.card {
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.card:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px 0 rgba(0, 0, 0, 0.15);
}

