// Chart.js default configuration
Chart.defaults.font.family = '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif';
Chart.defaults.font.size = 11;
Chart.defaults.color = '#6b7280';

// Color palette
const colors = {
  primary: '#3b82f6',
  secondary: '#8b5cf6',
  tertiary: '#06b6d4',
  accent: '#ec4899',
  success: '#10b981',
  warning: '#f59e0b',
  error: '#ef4444',
  purple: '#3730a3'
};

// Sessions by device donut chart
const sessionsDeviceCtx = document.getElementById('sessionsDeviceChart').getContext('2d');
new Chart(sessionsDeviceCtx, {
  type: 'doughnut',
  data: {
    labels: ['Mobile', 'Desktop', 'Tablet', 'Other'],
    datasets: [{
      data: [35000, 32000, 26000, 9000],
      backgroundColor: [colors.primary, colors.secondary, colors.tertiary, colors.accent],
      borderWidth: 0,
      cutout: '70%'
    }]
  },
  options: {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            return context.label + ': ' + context.parsed.toLocaleString();
          }
        }
      }
    }
  }
});

// Customers over time area chart
const customersTimeCtx = document.getElementById('customersTimeChart').getContext('2d');
new Chart(customersTimeCtx, {
  type: 'line',
  data: {
    labels: ['Jun 6', 'Jun 14', 'Jun 22', 'Jul 5'],
    datasets: [{
      label: 'First time',
      data: [3000, 4000, 6000, 4000],
      borderColor: colors.tertiary,
      backgroundColor: colors.tertiary + '30',
      fill: true,
      tension: 0.4,
      borderWidth: 2
    }, {
      label: 'Recurring',
      data: [3500, 5000, 7000, 5000],
      borderColor: colors.secondary,
      backgroundColor: colors.secondary + '30',
      fill: true,
      tension: 0.4,
      borderWidth: 2
    }]
  },
  options: {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        grid: {
          display: false
        },
        ticks: {
          font: {
            size: 10
          }
        }
      },
      y: {
        beginAtZero: true,
        grid: {
          color: '#f1f5f9'
        },
        ticks: {
          font: {
            size: 10
          },
          callback: function (value) {
            return value >= 1000 ? value / 1000 + 'K' : value;
          }
        }
      }
    },
    plugins: {
      legend: {
        display: true,
        position: 'bottom',
        labels: {
          usePointStyle: true,
          padding: 15,
          font: {
            size: 10
          }
        }
      }
    },
    elements: {
      point: {
        radius: 0,
        hoverRadius: 3
      }
    }
  }
});

// Total sales line chart
const totalSalesCtx = document.getElementById('totalSalesChart').getContext('2d');
new Chart(totalSalesCtx, {
  type: 'line',
  data: {
    labels: ['Jun 6', 'Jun 14', 'Jun 22', 'Jul 5'],
    datasets: [{
      data: [50000, 80000, 65000, 90000],
      borderColor: colors.primary,
      backgroundColor: colors.primary + '10',
      fill: true,
      tension: 0.4,
      borderWidth: 2
    }]
  },
  options: {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        display: false
      },
      y: {
        display: false
      }
    },
    plugins: {
      legend: {
        display: false
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            return '₹' + context.parsed.y / 1000 + 'K';
          }
        }
      }
    },
    elements: {
      point: {
        radius: 0,
        hoverRadius: 3
      }
    }
  }
});

// Sessions over time bar chart
const sessionsOverTimeCtx = document.getElementById('sessionsOverTimeChart').getContext('2d');
new Chart(sessionsOverTimeCtx, {
  type: 'bar',
  data: {
    labels: ['Jun 6', 'Jun 14', 'Jun 22', 'Jul 5'],
    datasets: [{
      label: 'Jun 6-Jul 5, 2023',
      data: [8000, 6000, 5000, 9000],
      backgroundColor: colors.primary,
      borderRadius: 3,
      barThickness: 15
    }, {
      label: 'Apr 22-Apr 28, 2024',
      data: [7000, 8000, 7000, 10000],
      backgroundColor: colors.tertiary,
      borderRadius: 3,
      barThickness: 15
    }]
  },
  options: {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        grid: {
          display: false
        },
        ticks: {
          font: {
            size: 10
          }
        }
      },
      y: {
        beginAtZero: true,
        grid: {
          color: '#f1f5f9'
        },
        ticks: {
          font: {
            size: 10
          },
          callback: function (value) {
            return value / 1000 + 'K';
          }
        }
      }
    },
    plugins: {
      legend: {
        display: false
      }
    }
  }
});

// Gross sales by device line chart
const grossSalesDeviceCtx = document.getElementById('grossSalesDeviceChart').getContext('2d');
new Chart(grossSalesDeviceCtx, {
  type: 'line',
  data: {
    labels: ['Jun 6', 'Jun 14', 'Jun 22', 'Jul 5'],
    datasets: [{
      label: 'Mobile',
      data: [8000, 7000, 9000, 10000],
      borderColor: colors.primary,
      backgroundColor: 'transparent',
      tension: 0.4,
      borderWidth: 2
    }, {
      label: 'Desktop',
      data: [6000, 8000, 7000, 8500],
      borderColor: colors.secondary,
      backgroundColor: 'transparent',
      tension: 0.4,
      borderWidth: 2
    }, {
      label: 'Tablet',
      data: [4000, 5000, 6000, 7000],
      borderColor: colors.tertiary,
      backgroundColor: 'transparent',
      tension: 0.4,
      borderWidth: 2
    }]
  },
  options: {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        grid: {
          display: false
        },
        ticks: {
          font: {
            size: 10
          }
        }
      },
      y: {
        beginAtZero: true,
        grid: {
          color: '#f1f5f9'
        },
        ticks: {
          font: {
            size: 10
          },
          callback: function (value) {
            return '₹' + value / 1000 + 'K';
          }
        }
      }
    },
    plugins: {
      legend: {
        display: true,
        position: 'bottom',
        labels: {
          usePointStyle: true,
          padding: 15,
          font: {
            size: 10
          }
        }
      }
    },
    elements: {
      point: {
        radius: 0,
        hoverRadius: 3
      }
    }
  }
});

// Sessions by country stacked bar chart
const sessionsCountryCtx = document.getElementById('sessionsCountryChart').getContext('2d');
new Chart(sessionsCountryCtx, {
  type: 'bar',
  data: {
    labels: ['Jun 6', 'Jun 14', 'Jun 22', 'Jul 5'],
    datasets: [{
      label: 'United States',
      data: [3000, 4000, 2500, 3500],
      backgroundColor: colors.primary,
      borderRadius: 0
    }, {
      label: 'Canada',
      data: [2000, 3000, 3000, 4000],
      backgroundColor: colors.secondary,
      borderRadius: 0
    }, {
      label: 'United Kingdom',
      data: [1500, 2000, 2500, 2000],
      backgroundColor: colors.tertiary,
      borderRadius: 0
    }, {
      label: 'France',
      data: [1000, 1500, 1000, 1500],
      backgroundColor: colors.accent,
      borderRadius: 0
    }]
  },
  options: {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        stacked: true,
        grid: {
          display: false
        },
        ticks: {
          font: {
            size: 10
          }
        }
      },
      y: {
        stacked: true,
        beginAtZero: true,
        grid: {
          color: '#f1f5f9'
        },
        ticks: {
          font: {
            size: 10
          },
          callback: function (value) {
            return value / 1000 + 'K';
          }
        }
      }
    },
    plugins: {
      legend: {
        display: false
      }
    }
  }
});

// Add loading animations
document.addEventListener('DOMContentLoaded', function () {
  // Animate horizontal bars
  setTimeout(() => {
    document.querySelectorAll('.bar-fill').forEach((bar, index) => {
      setTimeout(() => {
        bar.style.transition = 'width 0.8s cubic-bezier(0.4, 0, 0.2, 1)';
        const targetWidth = bar.style.width;
        bar.style.width = '0%';
        setTimeout(() => {
          bar.style.width = targetWidth;
        }, 100);
      }, index * 150);
    });
  }, 500);

  // Animate product bars
  setTimeout(() => {
    document.querySelectorAll('.color-bar').forEach((bar, index) => {
      setTimeout(() => {
        bar.style.transition = 'transform 0.6s cubic-bezier(0.4, 0, 0.2, 1)';
        bar.style.transform = 'scaleX(0)';
        bar.style.transformOrigin = 'left';
        setTimeout(() => {
          bar.style.transform = 'scaleX(1)';
        }, 100);
      }, index * 80);
    });
  }, 800);
});

