srcDoc.css:
.filter-container {
  display: flex;
  flex-wrap: wrap;
  gap: 15px;
  background: #f5f5f3;
  padding: 15px;
  border-radius: 12px;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
}

.filter-box {
  display: flex;
  flex-direction: column;
  gap: 5px;
}

label {
  font-size: 14px;
  font-weight: bold;
  color: #333;
}

select, input[type=date] {
  padding: 6px;
  border-radius: 6px;
  border: 1px solid #ccc;
  min-width: 140px;
}

button {
  padding: 8px 14px;
  border: none;
  background: #36A2EB;
  color: white;
  border-radius: 6px;
  cursor: pointer;
  align-self: flex-end;
  transition: background 0.2s;
}

button:hover {
  background: #1d8fd6;
}

srcDoc.js:
document.getElementById("applyFilters").addEventListener("click", () => {
  const filters = {
    startDate: document.getElementById("startDate").value,
    endDate: document.getElementById("endDate").value,
    device: document.getElementById("device").value,
    country: document.getElementById("country").value,
    product: document.getElementById("product").value
  };

  // Send filters to Appsmith store (so charts/tables can use them)
  appsmith.store.filters = filters;

  // Debug
  console.log("Applied Filters:", filters);
});

