srcDoc.css:
/* Reset */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: Times New Roman;
  background: #f5f5f3;
}

/* Header layout */
.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px 40px;
  background: #f5f5f3;
}

/* Left side (logo + nav) */
.left-side {
  display: flex;
  align-items: center;
  gap: 40px; /* gap between logo and nav links */
}

.logo img {
  height: 50px;
}

.nav-links {
  display: flex;
  gap: 25px;
}

.nav-links a {
  text-decoration: none;
  color: #000;
  font-size: 20px;
}

.nav-links a:hover {
  text-decoration: underline;
}

/* Right side (Log in + Button) */
.right-side {
  display: flex;
  align-items: center;
  gap: 20px;
}

.login {
  text-decoration: none;
  color: #000;
  font-size: 20px;
}

.btn {
  background: #000;
  color: #fff;
  padding: 8px 18px;
  border-radius: 20px;
  text-decoration: none;
  font-size: 14px;
  transition: 0.3s;
}

.btn:hover {
  background: #333;
}

